<?php

require_once('simpletest/autorun.php');
require_once(dirname(__FILE__) . '/../Postmark.php');

class AttachmentTests extends UnitTestCase
{
	private $_mail;
	private $_files;
	
	public function __construct()
	{
		$this->_files = array(dirname(__FILE__) . '/file.txt');
		
		file_put_contents($this->_files[0], str_repeat('1', 1024));		// 1 kB
		
		$arguments = func_get_args();
		call_user_func_array(array('parent', '__construct'), $arguments);
	}
	
	public function __destruct()
	{
		foreach ($this->_files as $filename) {
			unlink($filename);
		}
	}
	
	public function setUp()
	{
		$this->_mail = Mail_Postmark::compose()
			->debug(Mail_Postmark::DEBUG_RETURN)
			->to('john@smith.com', 'John Smith')
			->subject('The subject')
			->messagePlain('Test message');
	}
	
	public function tearDown()
	{
		unset($this->_mail);
	}
	
	
	public function testMissingFile()
	{
		$this->expectException('InvalidArgumentException');
		$debugData = $this->_mail
			->addAttachment('no-file')
			->send();
	}
	
	public function testSingleSmallFile()
	{
		$debugData = $this->_mail
			->addAttachment($this->_files[0])
			->send();
		
		#$this->dump($debugData);die;
		$this->assertEqual($debugData['json'], '{"Subject":"The subject","From":"\"Foo Bar\" <foo@bar.com>","To":"\"John Smith\" <john@smith.com>","TextBody":"Test message","Attachments":[{"Name":"file.txt","Content":"","ContentType":"text\/plain"}]}');
	}
	
	public function testMultipleSmallFile()
	{
		$debugData = $this->_mail
			->addAttachment($this->_files[0], array('filenameAlias' => 'file1.txt'))
			->addAttachment($this->_files[0], array('filenameAlias' => 'file2.txt'))
			->send();
		
		#$this->dump($debugData);
		$this->assertEqual($debugData['json'], '{"Subject":"The subject","From":"\"Foo Bar\" <foo@bar.com>","To":"\"John Smith\" <john@smith.com>","TextBody":"Test message","Attachments":[{"Name":"file1.txt","Content":"","ContentType":"text\/plain"},{"Name":"file2.txt","Content":"","ContentType":"text\/plain"}]}');
	}
}